import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql, relations } from "drizzle-orm";
import { 
  pgTable, 
  text, 
  varchar, 
  timestamp, 
  integer, 
  decimal,
  boolean,
  primaryKey,
  jsonb,
  index
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// Users table - Updated for Replit Auth compatibility
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(), // Required by Replit Auth, nullable
  firstName: varchar("first_name"), // Required by Replit Auth, nullable
  lastName: varchar("last_name"), // Required by Replit Auth, nullable  
  profileImageUrl: varchar("profile_image_url"), // Required by Replit Auth, nullable
  // Additional fields for our application
  username: text("username").unique(),
  fullName: text("full_name"),
  major: text("major"),
  role: text("role").default("student").notNull(), // student or admin
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(), // Required by Replit Auth
});

// Resources table
export const resources = pgTable("resources", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  subject: text("subject").notNull(),
  semester: text("semester"),
  fileType: text("file_type").notNull(),
  fileName: text("file_name").notNull(),
  fileSize: integer("file_size").notNull(),
  filePath: text("file_path").notNull(),
  uploadedById: varchar("uploaded_by_id").references(() => users.id).notNull(),
  downloadCount: integer("download_count").default(0),
  averageRating: decimal("average_rating", { precision: 3, scale: 2 }).default("0"),
  ratingCount: integer("rating_count").default(0),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Ratings table
export const ratings = pgTable("ratings", {
  resourceId: varchar("resource_id").references(() => resources.id).notNull(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  rating: integer("rating").notNull(), // 1-5 stars
  review: text("review"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
}, (table) => ({
  // Ensure one rating per user per resource
  pk: primaryKey({ columns: [table.resourceId, table.userId] }),
}));

// Tags table
export const tags = pgTable("tags", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull().unique(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Resource-Tags junction table
export const resourceTags = pgTable("resource_tags", {
  resourceId: varchar("resource_id").references(() => resources.id).notNull(),
  tagId: varchar("tag_id").references(() => tags.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
}, (table) => ({
  pk: primaryKey({ columns: [table.resourceId, table.tagId] }),
}));

// Favorites table
export const favorites = pgTable("favorites", {
  userId: varchar("user_id").references(() => users.id).notNull(),
  resourceId: varchar("resource_id").references(() => resources.id).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
}, (table) => ({
  // Ensure one favorite per user per resource
  pk: primaryKey({ columns: [table.userId, table.resourceId] }),
}));

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  resources: many(resources),
  ratings: many(ratings),
  favorites: many(favorites),
}));

export const resourcesRelations = relations(resources, ({ one, many }) => ({
  uploadedBy: one(users, {
    fields: [resources.uploadedById],
    references: [users.id],
  }),
  ratings: many(ratings),
  resourceTags: many(resourceTags),
  favorites: many(favorites),
}));

export const ratingsRelations = relations(ratings, ({ one }) => ({
  resource: one(resources, {
    fields: [ratings.resourceId],
    references: [resources.id],
  }),
  user: one(users, {
    fields: [ratings.userId],
    references: [users.id],
  }),
}));

export const tagsRelations = relations(tags, ({ many }) => ({
  resourceTags: many(resourceTags),
}));

export const resourceTagsRelations = relations(resourceTags, ({ one }) => ({
  resource: one(resources, {
    fields: [resourceTags.resourceId],
    references: [resources.id],
  }),
  tag: one(tags, {
    fields: [resourceTags.tagId],
    references: [tags.id],
  }),
}));

export const favoritesRelations = relations(favorites, ({ one }) => ({
  user: one(users, {
    fields: [favorites.userId],
    references: [users.id],
  }),
  resource: one(resources, {
    fields: [favorites.resourceId],
    references: [resources.id],
  }),
}));

// Zod schemas
export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  email: true,
  fullName: true,
  major: true,
  role: true,
});

export const insertResourceSchema = createInsertSchema(resources).pick({
  title: true,
  description: true,
  subject: true,
  semester: true,
  fileType: true,
  fileName: true,
  fileSize: true,
  filePath: true,
  uploadedById: true,
});

export const insertRatingSchema = createInsertSchema(ratings).pick({
  resourceId: true,
  userId: true,
  rating: true,
  review: true,
});

export const insertTagSchema = createInsertSchema(tags).pick({
  name: true,
});

export const insertFavoriteSchema = createInsertSchema(favorites).pick({
  userId: true,
  resourceId: true,
});

// TypeScript types
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type UpsertUser = typeof users.$inferInsert; // Required by Replit Auth

export type InsertResource = z.infer<typeof insertResourceSchema>;
export type Resource = typeof resources.$inferSelect;

export type InsertRating = z.infer<typeof insertRatingSchema>;
export type Rating = typeof ratings.$inferSelect;

export type InsertTag = z.infer<typeof insertTagSchema>;
export type Tag = typeof tags.$inferSelect;

export type InsertFavorite = z.infer<typeof insertFavoriteSchema>;
export type Favorite = typeof favorites.$inferSelect;

// Extended types with relations
export type ResourceWithDetails = Omit<Resource, "averageRating" | "ratingCount"> & {
  uploadedBy: User;
  ratings: Rating[];
  resourceTags: (typeof resourceTags.$inferSelect & { tag: Tag })[];
  averageRating: number;
  ratingCount: number;
};

export type UserWithStats = User & {
  resourceCount: number;
  totalDownloads: number;
  averageRating: number;
};

client/src/App.tsx.content:
import { useState } from "react";
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider, useQuery } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { SidebarProvider } from "@/components/ui/sidebar";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { BookOpen, Upload, AlertCircle } from "lucide-react";
import { Link } from "wouter";
import { useAuth } from "@/hooks/useAuth";

// Components
import Landing from "./components/Landing";
import Home from "./components/Home";
import Dashboard from "./components/Dashboard";
import UploadResource from "./components/UploadResource";
import SearchFilters from "./components/SearchFilters";
import ResourceCard from "./components/ResourceCard";
import AppSidebar from "./components/AppSidebar";
import Header from "./components/Header";
import NotFound from "@/pages/not-found";


interface Resource {
  id: string;
  title: string;
  description: string | null;
  subject: string;
  semester: string | null;
  fileType: string;
  fileName: string;
  fileSize: number;
  filePath: string;
  uploadedById: string;
  downloadCount: number | null;
  averageRating: string | null;
  ratingCount: number | null;
  isActive: boolean | null;
  createdAt: Date | null;
  updatedAt: Date | null;
  uploadedBy?: {
    firstName?: string;
    lastName?: string;
    username?: string;
  };
}

function BrowseResources() {
  const [filters, setFilters] = useState({
    query: '',
    subject: '',
    minRating: '',
    sortBy: 'newest' as const
  });

  // Fetch resources with current filters
  const { data: resources, isLoading } = useQuery({
    queryKey: ['/api/resources', filters],
    queryFn: () => {
      const params = new URLSearchParams();

      if (filters.query) params.append('search', filters.query);
      if (filters.subject && filters.subject !== "All Subjects") {
        params.append('subject', filters.subject);
      }
      if (filters.minRating && filters.minRating !== "all") {
        params.append('minRating', filters.minRating);
      }
      params.append('sortBy', filters.sortBy);
      params.append('limit', '50'); // Reasonable limit for browsing

      const queryString = params.toString();
      return fetch(`/api/resources${queryString ? '?' + queryString : ''}`)
        .then(res => res.json());
    },
  });

  const handleSearch = (newFilters: typeof filters) => {
    console.log("Updating filters:", newFilters);
    setFilters({
      query: newFilters.query || '',
      subject: newFilters.subject || '',
      minRating: newFilters.minRating || '',
      sortBy: newFilters.sortBy || 'newest'
    });
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-medium text-foreground">Browse Resources</h1>
        <p className="text-muted-foreground">Discover study materials shared by your peers</p>
      </div>

      <SearchFilters onSearch={handleSearch} />

      <div>
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-medium">
            {isLoading ? 'Loading...' : `${resources?.length || 0} resources found`}
          </h2>
        </div>

        {isLoading ? (
          // Loading skeleton
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {Array.from({ length: 6 }).map((_, index) => (
              <Card key={index} className="hover-elevate">
                <CardHeader>
                  <div className="h-5 w-3/4 bg-muted animate-pulse rounded mb-2" />
                  <div className="h-4 w-full bg-muted animate-pulse rounded" />
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    <div className="h-4 w-1/2 bg-muted animate-pulse rounded" />
                    <div className="h-4 w-1/3 bg-muted animate-pulse rounded" />
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : resources && resources.length > 0 ? (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {resources.map((resource: Resource) => (
              <ResourceCard key={resource.id} resource={resource} />
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <BookOpen className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
            <p className="text-muted-foreground">No resources found matching your criteria.</p>
            <p className="text-sm text-muted-foreground mt-2">Try adjusting your filters or search terms.</p>
          </div>
        )}
      </div>
    </div>
  );
}

function Favorites({ user }: { user: any }) {
  // Fetch user's favorite resources
  const { data: favorites, isLoading, error, refetch } = useQuery({
    queryKey: ['/api/users/me/favorites', user?.id],
    queryFn: async () => {
      const res = await fetch('/api/users/me/favorites', {
        credentials: 'include'
      });
      if (!res.ok) {
        throw new Error(`Failed to fetch favorites: ${res.status} ${res.statusText}`);
      }
      return res.json();
    },
    enabled: !!user?.id, // Only fetch if user is authenticated
  });

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-medium text-foreground">Favorites</h1>
        <p className="text-muted-foreground">Resources you've saved for later</p>
      </div>

      {error ? (
        <div className="text-center py-12" data-testid="favorites-error-state">
          <AlertCircle className="mx-auto h-12 w-12 text-destructive mb-4" />
          <h3 className="text-lg font-medium text-foreground mb-2">Failed to load favorites</h3>
          <p className="text-muted-foreground mb-4">{error instanceof Error ? error.message : 'An unexpected error occurred'}</p>
          <Button variant="outline" onClick={() => refetch()} data-testid="button-retry-favorites">
            Try Again
          </Button>
        </div>
      ) : isLoading ? (
        // Loading skeleton
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3" data-testid="favorites-loading-skeleton">
          {Array.from({ length: 6 }).map((_, index) => (
            <Card key={index} className="hover-elevate">
              <CardHeader>
                <div className="h-5 w-3/4 bg-muted animate-pulse rounded mb-2" />
                <div className="h-4 w-full bg-muted animate-pulse rounded" />
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-4 w-1/2 bg-muted animate-pulse rounded" />
                  <div className="h-4 w-1/3 bg-muted animate-pulse rounded" />
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : favorites && Array.isArray(favorites) && favorites.length > 0 ? (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3" data-testid="favorites-grid">
          {favorites.map((resource: Resource) => (
            <ResourceCard key={resource.id} resource={resource} data-testid={`favorite-resource-${resource.id}`} />
          ))}
        </div>
      ) : (
        <div className="text-center py-12" data-testid="favorites-empty-state">
          <BookOpen className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
          <p className="text-muted-foreground">No favorite resources yet.</p>
          <p className="text-sm text-muted-foreground mt-2">Star resources to save them here.</p>
          <Link href="/browse">
            <Button className="mt-4" data-testid="browse-resources-button">
              <BookOpen className="mr-2 h-4 w-4" />
              Browse Resources
            </Button>
          </Link>
        </div>
      )}
    </div>
  );
}

function MyResources({ user }: { user: any }) {
  // Fetch user's own resources
  const { data: userResources, isLoading } = useQuery({
    queryKey: ['/api/resources', { userId: user?.id }],
    queryFn: () => {
      const params = new URLSearchParams();
      if (user?.id) {
        params.append('userId', user.id);
      }
      params.append('sortBy', 'newest');

      const queryString = params.toString();
      return fetch(`/api/resources${queryString ? '?' + queryString : ''}`)
        .then(res => res.json());
    },
    enabled: !!user?.id, // Only fetch if user ID is available
  });

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-medium text-foreground">My Resources</h1>
        <p className="text-muted-foreground">Resources you've uploaded to the community</p>
      </div>

      {isLoading ? (
        // Loading skeleton
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {Array.from({ length: 3 }).map((_, index) => (
            <Card key={index} className="hover-elevate">
              <CardHeader>
                <div className="h-5 w-3/4 bg-muted animate-pulse rounded mb-2" />
                <div className="h-4 w-full bg-muted animate-pulse rounded" />
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-4 w-1/2 bg-muted animate-pulse rounded" />
                  <div className="h-4 w-1/3 bg-muted animate-pulse rounded" />
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : userResources && userResources.length > 0 ? (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {userResources.map((resource: Resource) => (
            <ResourceCard key={resource.id} resource={resource} />
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <BookOpen className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
          <p className="text-muted-foreground">You haven't uploaded any resources yet.</p>
          <p className="text-sm text-muted-foreground mt-2">Share your knowledge with the community!</p>
          <Button className="mt-4" onClick={() => {/* TODO: Navigate to upload */}}>
            <Upload className="mr-2 h-4 w-4" />
            Upload Your First Resource
          </Button>
        </div>
      )}
    </div>
  );
}

function AuthenticatedRouter({ user }: { user: any }) {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/dashboard" component={Dashboard} />
      <Route path="/browse">
        <BrowseResources />
      </Route>
      <Route path="/upload">
        <UploadResource />
      </Route>
      <Route path="/my-resources">
        <MyResources user={user} />
      </Route>
      <Route path="/favorites">
        <Favorites user={user} />
      </Route>
      <Route component={NotFound} />
    </Switch>
  );
}

function UnauthenticatedRouter() {
  return (
    <Switch>
      <Route path="/" component={Landing} />
      <Route component={Landing} />
    </Switch>
  );
}

function AuthenticatedApp({ user }: { user: any }) {
  const [theme, setTheme] = useState<"light" | "dark">("light");

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    document.documentElement.classList.toggle("dark", newTheme === "dark");
  };

  // Custom sidebar width
  const style = {
    "--sidebar-width": "16rem",
    "--sidebar-width-icon": "3rem",
  };

  // Get user display name
  const getUserDisplayName = () => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    if (user?.fullName) {
      return user.fullName;
    }
    if (user?.username) {
      return user.username;
    }
    if (user?.email) {
      return user.email;
    }
    return "User";
  };

  // Get user initials
  const getUserInitials = () => {
    const displayName = getUserDisplayName();
    return displayName.split(' ')
      .map((name: string) => name[0])
      .join('')
      .toUpperCase()
      .slice(0, 2) || "U";
  };

  return (
    <SidebarProvider style={style as React.CSSProperties}>
      <div className="flex h-screen w-full">
        <AppSidebar onNavigate={(path) => window.location.pathname = path} />
        <div className="flex flex-col flex-1 overflow-hidden">
          <Header 
            theme={theme}
            onThemeToggle={toggleTheme}
            onSearch={(query) => console.log("Global search:", query)}
            userName={getUserDisplayName()}
            userInitials={getUserInitials()}
            notificationCount={0}
            onLogout={() => window.location.href = '/api/logout'}
          />
          <main className="flex-1 overflow-auto p-6 bg-background">
            <div className="max-w-7xl mx-auto">
              <AuthenticatedRouter user={user} />
            </div>
          </main>
        </div>
      </div>
    </SidebarProvider>
  );
}

function AppContent() {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
          <p className="mt-2 text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  return isAuthenticated ? <AuthenticatedApp user={user} /> : <UnauthenticatedRouter />;
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <AppContent />
        <Toaster />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

