              data-testid="button-settings"
            >
              <Settings className="h-4 w-4 mr-1" />
              Settings
            </Button>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={handleLogout}
              data-testid="button-logout"
            >
              <LogOut className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </SidebarFooter>

      <SidebarRail />
    </Sidebar>
  );
}

client/src/components/Dashboard.tsx.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { BookOpen, Download, Star, Upload, TrendingUp, Users } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import ResourceCard from "@/components/ResourceCard";

export default function Dashboard() {
  // Fetch dashboard stats
  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ['/api/stats'],
    queryFn: () => fetch('/api/stats').then(res => res.json()),
  });

  // Fetch recent resources (limit to 6 for dashboard)
  const { data: recentResources, isLoading: resourcesLoading } = useQuery({
    queryKey: ['/api/resources', { limit: 6, sortBy: 'newest' }],
    queryFn: () => fetch('/api/resources?limit=6&sortBy=newest').then(res => res.json()),
  });

  // Transform stats data for display
  const dashboardStats = (stats && typeof stats === 'object') ? [
    { 
      title: "Total Resources", 
      value: stats.totalResources?.toString() || "0", 
      icon: BookOpen, 
      change: "+12%" // TODO: Calculate actual change
    },
    { 
      title: "Total Downloads", 
      value: stats.totalDownloads?.toString() || "0", 
      icon: Download, 
      change: "+23%" // TODO: Calculate actual change
    },
    { 
      title: "Average Rating", 
      value: stats.averageRating ? stats.averageRating.toFixed(1) : "0.0", 
      icon: Star, 
      change: "+0.2" // TODO: Calculate actual change
    },
    { 
      title: "Active Contributors", 
      value: stats.activeUsers?.toString() || "0", 
      icon: Users, 
      change: "+8%" // TODO: Calculate actual change
    },
  ] : [];
  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-medium text-foreground">Welcome back!</h1>
        <p className="text-muted-foreground">Here's what's happening in your study hub today.</p>
      </div>

      {/* Stats Grid */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        {statsLoading ? (
          // Loading skeleton for stats
          Array.from({ length: 4 }).map((_, index) => (
            <Card key={index} className="hover-elevate">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <div className="h-4 w-24 bg-muted animate-pulse rounded" />
                <div className="h-4 w-4 bg-muted animate-pulse rounded" />
              </CardHeader>
              <CardContent>
                <div className="h-8 w-16 bg-muted animate-pulse rounded mb-2" />
                <div className="h-3 w-20 bg-muted animate-pulse rounded" />
              </CardContent>
            </Card>
          ))
        ) : (
          dashboardStats.map((stat, index) => (
            <Card key={index} className="hover-elevate">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">{stat.title}</CardTitle>
                <stat.icon className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-medium">{stat.value}</div>
                <div className="flex items-center text-xs text-muted-foreground">
                  <TrendingUp className="mr-1 h-3 w-3" />
                  {stat.change} from last week
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {/* Quick Actions */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Quick Actions</CardTitle>
          <CardDescription>Jump into the most common tasks</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex gap-4">
            <Button data-testid="button-upload-resource" className="flex-1">
              <Upload className="mr-2 h-4 w-4" />
              Upload Resource
            </Button>
            <Button variant="outline" data-testid="button-browse-resources" className="flex-1">
              <BookOpen className="mr-2 h-4 w-4" />
              Browse Resources
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Recent Resources */}
      <div>
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-medium">Recent Resources</h2>
          <Button variant="ghost" size="sm" data-testid="link-view-all">
            View all
          </Button>
        </div>
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {resourcesLoading ? (
            // Loading skeleton for resources
            Array.from({ length: 6 }).map((_, index) => (
              <Card key={index} className="hover-elevate">
                <CardHeader>
                  <div className="h-5 w-3/4 bg-muted animate-pulse rounded mb-2" />
                  <div className="h-4 w-full bg-muted animate-pulse rounded" />
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    <div className="h-4 w-1/2 bg-muted animate-pulse rounded" />
                    <div className="h-4 w-1/3 bg-muted animate-pulse rounded" />
                  </div>
                </CardContent>
              </Card>
            ))
          ) : recentResources && Array.isArray(recentResources) && recentResources.length > 0 ? (
            recentResources.map((resource: any) => (
              <ResourceCard key={resource.id} resource={resource} />
            ))
          ) : (
            <div className="col-span-full text-center py-8 text-muted-foreground">
              <BookOpen className="mx-auto h-8 w-8 mb-2" />
              <p>No resources available yet. Be the first to upload!</p>
            </div>
          )}
        </div>
      </div>

      {/* Popular Subjects */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Popular Subjects This Week</CardTitle>
          <CardDescription>Most active subjects in your network</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-2">
            {["Computer Science", "Mathematics", "Physics", "Chemistry", "Biology", "Economics", "Psychology", "History"].map((subject) => (
              <Badge 
                key={subject} 
                variant="secondary" 
                className="hover-elevate cursor-pointer" 
                data-testid={`subject-${subject.toLowerCase().replace(' ', '-')}`}
              >
                {subject}
              </Badge>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/Header.tsx.content:
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuLabel, 
  DropdownMenuSeparator, 
  DropdownMenuTrigger 
} from "@/components/ui/dropdown-menu";
import { SidebarTrigger } from "@/components/ui/sidebar";
import { Badge } from "@/components/ui/badge";
import { 
  Search, 
  Bell, 
  Settings, 
  User, 
  LogOut, 
  Moon, 
  Sun,
  Upload
} from "lucide-react";
import { useState } from "react";

interface HeaderProps {
  onSearch?: (query: string) => void;
  onThemeToggle?: () => void;
  onLogout?: () => void;
  theme?: "light" | "dark";
  userName?: string;
  userInitials?: string;
  notificationCount?: number;
}

export default function Header({ 
  onSearch,
  onThemeToggle,
  onLogout,
  theme = "light",
  userName = "John Doe",
  userInitials = "JD",
  notificationCount = 3
}: HeaderProps) {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Header search:", searchQuery);
    onSearch?.(searchQuery);
  };

  const handleProfileAction = (action: string) => {
    console.log("Profile action:", action);
    if (action === 'logout' && onLogout) {
      onLogout();
    }
  };

  const handleNotification = () => {
    console.log("Notifications clicked");
  };

  return (
    <header className="sticky top-0 z-50 w-full border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
      <div className="flex h-14 items-center px-4">
        {/* Sidebar Toggle */}
        <SidebarTrigger className="mr-4" data-testid="button-sidebar-toggle" />

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="flex-1 max-w-md">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
            <Input
              placeholder="Search resources..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 pr-4"
              data-testid="input-header-search"
            />
          </div>
        </form>

        {/* Right Side Actions */}
        <div className="flex items-center space-x-2 ml-4">
          {/* Upload Button */}
          <Button 
            size="sm" 
            variant="outline" 
            className="hidden md:flex"
            onClick={() => console.log("Upload clicked")}
            data-testid="button-header-upload"
          >
            <Upload className="h-4 w-4 mr-2" />
            Upload
          </Button>

          {/* Theme Toggle */}
          <Button
            variant="ghost"
            size="icon"
            onClick={onThemeToggle}
            data-testid="button-theme-toggle"
          >
            {theme === "light" ? (
              <Moon className="h-4 w-4" />
            ) : (
              <Sun className="h-4 w-4" />
            )}
          </Button>

          {/* Notifications */}
          <Button
            variant="ghost"
            size="icon"
            className="relative"
            onClick={handleNotification}
            data-testid="button-notifications"
          >
            <Bell className="h-4 w-4" />
            {notificationCount > 0 && (
              <Badge 
                variant="destructive" 
                className="absolute -top-1 -right-1 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs"
                data-testid="notification-badge"
              >
                {notificationCount > 9 ? '9+' : notificationCount}
              </Badge>
            )}
          </Button>

          {/* User Menu */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="relative h-8 w-8 rounded-full" data-testid="button-user-menu">
                <Avatar className="h-8 w-8">
                  <AvatarFallback className="text-xs">
                    {userInitials}
                  </AvatarFallback>
                </Avatar>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="w-56" align="end" forceMount>
              <DropdownMenuLabel className="font-normal">
                <div className="flex flex-col space-y-1">
                  <p className="text-sm font-medium leading-none" data-testid="dropdown-username">
                    {userName}
                  </p>
                  <p className="text-xs leading-none text-muted-foreground">
                    Computer Science Student
                  </p>
                </div>
              </DropdownMenuLabel>
              <DropdownMenuSeparator />
              <DropdownMenuItem 
                onClick={() => handleProfileAction('profile')}
                data-testid="dropdown-profile"
              >
                <User className="mr-2 h-4 w-4" />
                <span>Profile</span>
              </DropdownMenuItem>
              <DropdownMenuItem 
                onClick={() => handleProfileAction('settings')}
                data-testid="dropdown-settings"
              >
                <Settings className="mr-2 h-4 w-4" />
                <span>Settings</span>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem 
                onClick={() => handleProfileAction('logout')}
                data-testid="dropdown-logout"
              >
                <LogOut className="mr-2 h-4 w-4" />
                <span>Log out</span>
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </header>
  );
}

client/src/components/RatingSystem.tsx.content:
import { Star } from "lucide-react";
import { cn } from "@/lib/utils";

interface RatingSystemProps {
  rating: number;
  onRating?: (rating: number) => void;
  size?: "sm" | "md" | "lg";
  readonly?: boolean;
  "data-testid"?: string;
}

export default function RatingSystem({ 
  rating, 
  onRating, 
  size = "md", 
  readonly = false,
  "data-testid": dataTestId
}: RatingSystemProps) {
  const sizeClasses = {
    sm: "h-3 w-3",
    md: "h-4 w-4", 
    lg: "h-5 w-5",
  };

  const handleStarClick = (starRating: number) => {
    if (!readonly && onRating) {
      onRating(starRating);
    }
  };

  return (
    <div className="flex items-center space-x-0.5" data-testid={dataTestId}>
      {[1, 2, 3, 4, 5].map((star) => (
        <button
          key={star}
          type="button"
          onClick={() => handleStarClick(star)}
          disabled={readonly}
          className={cn(
            "transition-colors duration-150",
            !readonly && "hover:scale-110 active:scale-95",
            readonly ? "cursor-default" : "cursor-pointer"
          )}
          data-testid={`star-${star}`}
        >
          <Star
            className={cn(
              sizeClasses[size],
              "transition-all duration-150",
              star <= rating 
                ? "fill-amber-400 text-amber-400" 
                : "fill-muted text-muted-foreground"
            )}
          />
        </button>
      ))}
    </div>
  );
}

client/src/components/ResourceCard.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Download, Star, FileText, File } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import RatingSystem from "@/components/RatingSystem";
import type { Resource } from "@shared/schema";

interface ResourceCardProps {
  resource: Resource;
}

const getFileIcon = (fileType: string) => {
  // Handle both MIME types and file extensions
  const type = fileType.toLowerCase();
  if (type.includes('pdf') || type === 'pdf') {
    return <FileText className="h-4 w-4 text-red-600" />;
  }
  if (type.includes('word') || type.includes('document') || 
      type === 'docx' || type === 'doc' || 
      type.includes('vnd.openxmlformats-officedocument.wordprocessingml.document') ||
      type.includes('msword')) {
    return <File className="h-4 w-4 text-blue-600" />;
  }
  if (type.includes('image') || type.includes('jpeg') || type.includes('png')) {
    return <File className="h-4 w-4 text-green-600" />;
  }
  return <File className="h-4 w-4 text-muted-foreground" />;
};

// Helper function to format relative time
const formatRelativeTime = (date: Date | string) => {
  const now = new Date();
  const resourceDate = new Date(date);
  const diffInMs = now.getTime() - resourceDate.getTime();
  const diffInHours = Math.floor(diffInMs / (1000 * 60 * 60));
  const diffInDays = Math.floor(diffInHours / 24);

  if (diffInHours < 1) return "Just now";
  if (diffInHours < 24) return `${diffInHours} hour${diffInHours > 1 ? 's' : ''} ago`;
  if (diffInDays < 7) return `${diffInDays} day${diffInDays > 1 ? 's' : ''} ago`;

  return resourceDate.toLocaleDateString();
};

// Helper function to get user display name
const getUserDisplayName = (user: any, fallbackId?: string) => {
  if (!user) return fallbackId ? `User ${fallbackId.slice(0, 8)}` : "Unknown User";
  if (typeof user === 'string') return user; // Handle legacy string format
  if (user.firstName && user.lastName) return `${user.firstName} ${user.lastName}`;
  if (user.fullName) return user.fullName;
  if (user.username) return user.username;
  if (user.email) return user.email;
  return "Unknown User";
};

export default function ResourceCard({ resource }: ResourceCardProps) {
  const { toast } = useToast();

  // Mutation for downloading file (this will handle both download and count increment)
  const downloadMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/resources/${resource.id}/download`, {
        method: 'GET',
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error(`Download failed: ${response.status} ${response.statusText}`);
      }

      // Get the filename from the response headers or use a default
      const contentDisposition = response.headers.get('Content-Disposition');
      let filename = resource.fileName;
      if (contentDisposition) {
        const filenameMatch = contentDisposition.match(/filename="?([^"]+)"?/);
        if (filenameMatch) {
          filename = filenameMatch[1];
        }
      }

      // Create blob and download
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      return { filename };
    },
    onSuccess: (data) => {
      // Invalidate resources queries to update download count
      queryClient.invalidateQueries({ queryKey: ['/api/resources'] });
      queryClient.invalidateQueries({ queryKey: ['/api/users/me/favorites'] });
      toast({
        title: "Download successful",
        description: `${data.filename} has been downloaded.`,
      });
    },
    onError: (error: Error) => {
      console.error('Download error:', error);
      toast({
        title: "Download failed",
        description: error.message || "Could not download the resource. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleDownload = () => {
    downloadMutation.mutate();
  };

  const handleRating = (rating: number) => {
    console.log("Rating resource:", resource.id, "with", rating, "stars");
    // TODO: Implement rating functionality
    toast({
      title: "Rating submitted",
      description: `You rated this resource ${rating} stars.`,
    });
  };

  return (
    <Card className="hover-elevate">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-2">
            {getFileIcon(resource.fileType)}
            <Badge variant="outline" className="text-xs">
              {resource.fileType}
            </Badge>
          </div>
          <Button 
            size="sm" 
            variant="outline" 
            onClick={handleDownload}
            disabled={downloadMutation.isPending}
            data-testid={`button-download-${resource.id}`}
          >
            <Download className="h-3 w-3 mr-1" />
            {resource.downloadCount || 0}
          </Button>
        </div>
        <CardTitle className="text-base line-clamp-2" data-testid={`title-resource-${resource.id}`}>
          {resource.title}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-3">
        <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`description-resource-${resource.id}`}>
          {resource.description}
        </p>

        <div className="flex flex-wrap gap-1">
          <Badge variant="secondary" className="text-xs" data-testid={`subject-${resource.subject.toLowerCase()}`}>
            {resource.subject}
          </Badge>
          {resource.semester && (
            <Badge variant="secondary" className="text-xs" data-testid={`semester-${resource.semester.toLowerCase().replace(' ', '-')}`}>
              {resource.semester}
            </Badge>
          )}
        </div>

        <div className="flex items-center justify-between">
          <RatingSystem 
            rating={parseFloat(resource.averageRating || "0")} 
            onRating={handleRating}
            size="sm"
            data-testid={`rating-resource-${resource.id}`}
          />
          <div className="flex items-center space-x-1 text-xs text-muted-foreground">
            <Star className="h-3 w-3 fill-current" />
            <span data-testid={`rating-value-${resource.id}`}>
              {parseFloat(resource.averageRating || "0").toFixed(1)}
            </span>
          </div>
        </div>

        <div className="flex items-center justify-between text-xs text-muted-foreground">
          <div className="flex items-center space-x-2">
            <Avatar className="h-5 w-5">
              <AvatarFallback className="text-xs">
                {getUserDisplayName((resource as any).uploadedBy || resource.uploadedById, resource.uploadedById).split(' ').map((n: string) => n[0]).join('').toUpperCase()}
              </AvatarFallback>
            </Avatar>
            <span data-testid={`uploader-${resource.id}`}>
              {getUserDisplayName((resource as any).uploadedBy || resource.uploadedById, resource.uploadedById)}
            </span>
          </div>
          <span data-testid={`uploaded-time-${resource.id}`}>
            {formatRelativeTime(resource.createdAt || new Date())}
          </span>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/SearchFilters.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Search, Filter, X, Star } from "lucide-react";

const subjects = [
  "All Subjects", "Mathematics", "Computer Science", "Physics", "Chemistry", 
  "Biology", "Economics", "Psychology", "History", "Literature", "Engineering"
];

const semesters = [
  "All Semesters", "Fall 2024", "Spring 2024", "Fall 2023", "Spring 2023", "Fall 2022", "Spring 2022"
];

const fileTypes = [
  { id: "pdf", label: "PDF" },
  { id: "doc", label: "Word Documents" },
  { id: "img", label: "Images" },
  { id: "ppt", label: "Presentations" },
];

const ratings = [
  { value: "all", label: "All Ratings" },
  { value: "4", label: "4+ Stars" },
  { value: "3", label: "3+ Stars" },
  { value: "2", label: "2+ Stars" },
];

interface SearchFiltersProps {
  onSearch?: (filters: any) => void;
}

export default function SearchFilters({ onSearch }: SearchFiltersProps) {
  const [filters, setFilters] = useState({
    query: "",
    subject: "All Subjects",
    semester: "All Semesters",
    fileTypes: [] as string[],
    minRating: "all",
    sortBy: "relevance",
  });

  const [showAdvanced, setShowAdvanced] = useState(false);

  const handleSearch = () => {
    console.log("Search with filters:", filters);
    onSearch?.(filters);
  };

  const clearFilters = () => {
    const clearedFilters = {
      query: "",
      subject: "All Subjects",
      semester: "All Semesters",
      fileTypes: [],
      minRating: "all",
      sortBy: "relevance",
    };
    setFilters(clearedFilters);
    onSearch?.(clearedFilters);
  };

  const toggleFileType = (fileType: string) => {
    setFilters({
      ...filters,
      fileTypes: filters.fileTypes.includes(fileType)
        ? filters.fileTypes.filter(t => t !== fileType)
        : [...filters.fileTypes, fileType]
    });
  };

  const activeFiltersCount = [
    filters.subject !== "All Subjects" ? 1 : 0,
    filters.semester !== "All Semesters" ? 1 : 0,
    filters.fileTypes.length,
    filters.minRating !== "all" ? 1 : 0,
  ].reduce((a, b) => a + b, 0);

  return (
    <div className="space-y-4">
      {/* Search Bar */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex space-x-2">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search for notes, papers, guides..."
                className="pl-10"
                value={filters.query}
                onChange={(e) => setFilters({ ...filters, query: e.target.value })}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                data-testid="input-search"
              />
            </div>
            <Button onClick={handleSearch} data-testid="button-search">
              <Search className="h-4 w-4 mr-2" />
              Search
            </Button>
            <Button
              variant="outline"
              onClick={() => setShowAdvanced(!showAdvanced)}
              data-testid="button-toggle-filters"
            >
              <Filter className="h-4 w-4 mr-2" />
              Filters
              {activeFiltersCount > 0 && (
                <Badge variant="secondary" className="ml-2 h-5 min-w-5 rounded-full text-xs">
                  {activeFiltersCount}
                </Badge>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Advanced Filters */}
      {showAdvanced && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-lg">Advanced Filters</CardTitle>
                <CardDescription>Refine your search results</CardDescription>
              </div>
              <Button variant="ghost" size="sm" onClick={clearFilters} data-testid="button-clear-filters">
                <X className="h-4 w-4 mr-1" />
                Clear All
              </Button>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Subject</Label>
                <Select 
                  value={filters.subject} 
                  onValueChange={(value) => setFilters({ ...filters, subject: value })}
                >
                  <SelectTrigger data-testid="select-filter-subject">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {subjects.map((subject) => (
                      <SelectItem key={subject} value={subject}>
                        {subject}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Semester</Label>
                <Select 
                  value={filters.semester} 
                  onValueChange={(value) => setFilters({ ...filters, semester: value })}
                >
                  <SelectTrigger data-testid="select-filter-semester">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {semesters.map((semester) => (
                      <SelectItem key={semester} value={semester}>
                        {semester}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-2">
              <Label>File Types</Label>
              <div className="flex flex-wrap gap-3">
                {fileTypes.map((fileType) => (
                  <div key={fileType.id} className="flex items-center space-x-2">
                    <Checkbox
                      id={fileType.id}
                      checked={filters.fileTypes.includes(fileType.id)}
                      onCheckedChange={() => toggleFileType(fileType.id)}
                      data-testid={`checkbox-${fileType.id}`}
                    />
                    <Label htmlFor={fileType.id} className="text-sm cursor-pointer">
                      {fileType.label}
                    </Label>
                  </div>
                ))}
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Minimum Rating</Label>
                <Select 
                  value={filters.minRating} 
                  onValueChange={(value) => setFilters({ ...filters, minRating: value })}
                >
                  <SelectTrigger data-testid="select-min-rating">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {ratings.map((rating) => (
                      <SelectItem key={rating.value} value={rating.value}>
                        <div className="flex items-center space-x-1">
                          <span>{rating.label}</span>
                          {rating.value !== "all" && (
                            <Star className="h-3 w-3 fill-amber-400 text-amber-400" />
                          )}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Sort By</Label>
                <Select 
                  value={filters.sortBy} 
                  onValueChange={(value) => setFilters({ ...filters, sortBy: value })}
                >
                  <SelectTrigger data-testid="select-sort-by">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="relevance">Relevance</SelectItem>
                    <SelectItem value="newest">Newest First</SelectItem>
                    <SelectItem value="oldest">Oldest First</SelectItem>
                    <SelectItem value="rating">Highest Rated</SelectItem>
                    <SelectItem value="downloads">Most Downloaded</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="flex justify-end space-x-2 pt-4">
              <Button variant="outline" onClick={clearFilters} data-testid="button-reset-filters">
                Reset
              </Button>
              <Button onClick={handleSearch} data-testid="button-apply-filters">
                Apply Filters
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Active Filters Display */}
      {activeFiltersCount > 0 && (
        <div className="flex flex-wrap gap-2">
          {filters.subject !== "All Subjects" && (
            <Badge variant="secondary" className="flex items-center gap-1" data-testid="active-filter-subject">
              Subject: {filters.subject}
              <Button
                variant="ghost"
                size="icon"
                className="h-3 w-3 p-0"
                onClick={() => setFilters({ ...filters, subject: "All Subjects" })}
              >
                <X className="h-2 w-2" />
              </Button>
            </Badge>
          )}
          {filters.semester !== "All Semesters" && (
            <Badge variant="secondary" className="flex items-center gap-1" data-testid="active-filter-semester">
              Semester: {filters.semester}
              <Button
                variant="ghost"
                size="icon"
                className="h-3 w-3 p-0"
                onClick={() => setFilters({ ...filters, semester: "All Semesters" })}
              >
                <X className="h-2 w-2" />
              </Button>
            </Badge>
          )}
          {filters.fileTypes.map((fileType) => (
            <Badge key={fileType} variant="secondary" className="flex items-center gap-1" data-testid={`active-filter-filetype-${fileType}`}>
              {fileTypes.find(f => f.id === fileType)?.label}
              <Button
                variant="ghost"
                size="icon"
                className="h-3 w-3 p-0"
                onClick={() => toggleFileType(fileType)}
              >
                <X className="h-2 w-2" />
              </Button>
            </Badge>
          ))}
          {filters.minRating !== "all" && (
            <Badge variant="secondary" className="flex items-center gap-1" data-testid="active-filter-rating">
              {ratings.find(r => r.value === filters.minRating)?.label}
              <Button
                variant="ghost"
                size="icon"
                className="h-3 w-3 p-0"
                onClick={() => setFilters({ ...filters, minRating: "all" })}
              >
                <X className="h-2 w-2" />
              </Button>
            </Badge>
          )}
        </div>
      )}
    </div>
  );
}

client/src/components/UploadResource.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { Upload, X, FileText, File, LogIn } from "lucide-react";

const subjects = [
  "Mathematics", "Computer Science", "Physics", "Chemistry", "Biology", 
  "Economics", "Psychology", "History", "Literature", "Engineering"
];

const semesters = [
  "Fall 2024", "Spring 2024", "Fall 2023", "Spring 2023", "Fall 2022", "Spring 2022"
];

export default function UploadResource() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    subject: "",
    semester: "",
    tags: [] as string[],
    currentTag: ""
  });
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [dragActive, setDragActive] = useState(false);
  const { toast } = useToast();
  const { user, isAuthenticated } = useAuth();

  // Show login prompt for unauthenticated users
  if (!isAuthenticated) {
    return (
      <div className="max-w-2xl mx-auto space-y-6">
        <div className="text-center space-y-4">
          <LogIn className="mx-auto h-12 w-12 text-muted-foreground" />
          <div>
            <h1 className="text-2xl font-medium text-foreground">Authentication Required</h1>
            <p className="text-muted-foreground">
              Please log in to upload and share study materials
            </p>
          </div>
          <Button 
            onClick={() => window.location.href = '/auth/login'}
            data-testid="button-login"
          >
            <LogIn className="mr-2 h-4 w-4" />
            Log In
          </Button>
        </div>
      </div>
    );
  }

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.size > 10 * 1024 * 1024) { // 10MB limit
        toast({
          title: "File too large",
          description: "Please select a file smaller than 10MB.",
          variant: "destructive",
        });
        return;
      }
      setSelectedFile(file);
    }
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const file = e.dataTransfer.files?.[0];
    if (file) {
      if (file.size > 10 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select a file smaller than 10MB.",
          variant: "destructive",
        });
        return;
      }
      setSelectedFile(file);
    }
  };

  const addTag = () => {
    if (formData.currentTag.trim() && !formData.tags.includes(formData.currentTag.trim())) {
      setFormData({
        ...formData,
        tags: [...formData.tags, formData.currentTag.trim()],
        currentTag: ""
      });
    }
  };

  const removeTag = (tagToRemove: string) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter(tag => tag !== tagToRemove)
    });
  };

  // Mutation for uploading resource with file
  const uploadMutation = useMutation({
    mutationFn: async ({ formData, file }: { formData: any, file: File }) => {
      const uploadFormData = new FormData();

      // Add the file
      uploadFormData.append('file', file);

      // Add metadata
      uploadFormData.append('title', formData.title.trim());
      uploadFormData.append('subject', formData.subject.trim());
      if (formData.description.trim()) {
        uploadFormData.append('description', formData.description.trim());
      }
      if (formData.semester.trim()) {
        uploadFormData.append('semester', formData.semester.trim());
      }
      if (formData.tags.length > 0) {
        uploadFormData.append('tags', JSON.stringify(formData.tags));
      }

      const response = await fetch('/api/resources', {
        method: 'POST',
        body: uploadFormData,
        credentials: 'include', // Include session cookies
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || 'Upload failed');
      }

      return response.json();
    },
    onSuccess: (data) => {
      // Invalidate resources queries to refresh the lists
      queryClient.invalidateQueries({ queryKey: ['/api/resources'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });

      toast({
        title: "Resource uploaded!",
        description: "Your resource has been shared with the community.",
      });

      // Reset form
      setFormData({ title: "", description: "", subject: "", semester: "", tags: [], currentTag: "" });
      setSelectedFile(null);

      console.log("Resource created:", data);
    },
    onError: (error: any) => {
      console.error("Upload error:", error);
      toast({
        title: "Upload failed",
        description: error.message || "Could not upload the resource. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedFile) {
      toast({
        title: "No file selected",
        description: "Please select a file to upload.",
        variant: "destructive",
      });
      return;
    }

    // Validate required fields
    if (!formData.title.trim() || !formData.subject.trim()) {
      toast({
        title: "Missing required fields",
        description: "Please fill in the title and subject.",
        variant: "destructive",
      });
      return;
    }

    uploadMutation.mutate({ formData, file: selectedFile });
  };

  const getFileIcon = (fileName: string) => {
    const extension = fileName.split('.').pop()?.toLowerCase();
    switch (extension) {
      case 'pdf':
        return <FileText className="h-8 w-8 text-red-600" />;
      case 'docx':
      case 'doc':
        return <File className="h-8 w-8 text-blue-600" />;
      default:
        return <File className="h-8 w-8 text-muted-foreground" />;
    }
  };

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      <div>
        <h1 className="text-2xl font-medium text-foreground">Upload Resource</h1>
        <p className="text-muted-foreground">Share your study materials with fellow students</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* File Upload */}
        <Card>
          <CardHeader>
            <CardTitle>Select File</CardTitle>
            <CardDescription>Upload PDF, Word documents, or images (max 10MB)</CardDescription>
          </CardHeader>
          <CardContent>
            {!selectedFile ? (
              <div
                className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
                  dragActive ? "border-primary bg-primary/5" : "border-muted-foreground/25"
                }`}
                onDragEnter={handleDrag}
                onDragLeave={handleDrag}
                onDragOver={handleDrag}
                onDrop={handleDrop}
              >
                <Upload className="mx-auto h-8 w-8 text-muted-foreground mb-4" />
                <p className="text-sm text-muted-foreground mb-4">
                  Drag and drop your file here, or click to browse
                </p>
                <input
                  type="file"
                  id="file-upload"
                  className="hidden"
                  accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                  onChange={handleFileChange}
                  data-testid="input-file"
                />
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => document.getElementById('file-upload')?.click()}
                  data-testid="button-browse-file"
                >
                  Browse Files
                </Button>
              </div>
            ) : (
              <div className="flex items-center space-x-4 p-4 bg-muted rounded-lg">
                {getFileIcon(selectedFile.name)}
                <div className="flex-1">
                  <p className="font-medium" data-testid="selected-filename">{selectedFile.name}</p>
                  <p className="text-sm text-muted-foreground">
                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
                <Button
                  type="button"
                  variant="ghost"
                  size="icon"
                  onClick={() => setSelectedFile(null)}
                  data-testid="button-remove-file"
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Resource Details */}
        <Card>
          <CardHeader>
            <CardTitle>Resource Details</CardTitle>
            <CardDescription>Provide information to help others find your resource</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="title">Title *</Label>
              <Input
                id="title"
                placeholder="Enter a descriptive title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                data-testid="input-title"
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Describe what this resource covers..."
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                data-testid="textarea-description"
                rows={3}
              />
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Subject *</Label>
                <Select 
                  value={formData.subject} 
                  onValueChange={(value) => setFormData({ ...formData, subject: value })}
                  required
                >
                  <SelectTrigger data-testid="select-subject">
                    <SelectValue placeholder="Select subject" />
                  </SelectTrigger>
                  <SelectContent>
                    {subjects.map((subject) => (
                      <SelectItem key={subject} value={subject}>
                        {subject}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Semester</Label>
                <Select 
                  value={formData.semester} 
                  onValueChange={(value) => setFormData({ ...formData, semester: value })}
                >
                  <SelectTrigger data-testid="select-semester">
                    <SelectValue placeholder="Select semester" />
                  </SelectTrigger>
                  <SelectContent>
                    {semesters.map((semester) => (
                      <SelectItem key={semester} value={semester}>
                        {semester}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-2">
              <Label>Tags</Label>
              <div className="flex space-x-2">
                <Input
                  placeholder="Add tags (e.g., midterm, notes, solutions)"
                  value={formData.currentTag}
                  onChange={(e) => setFormData({ ...formData, currentTag: e.target.value })}
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addTag())}
                  data-testid="input-tag"
                />
                <Button type="button" variant="outline" onClick={addTag} data-testid="button-add-tag">
                  Add
                </Button>
              </div>
              {formData.tags.length > 0 && (
                <div className="flex flex-wrap gap-2 mt-2">
                  {formData.tags.map((tag, index) => (
                    <Badge 
                      key={index} 
                      variant="secondary" 
                      className="flex items-center gap-1"
                      data-testid={`tag-${tag.toLowerCase().replace(' ', '-')}`}
                    >
                      {tag}
                      <Button
                        type="button"
                        variant="ghost"
                        size="icon"
                        className="h-3 w-3 p-0 hover:bg-destructive hover:text-destructive-foreground"
                        onClick={() => removeTag(tag)}
                        data-testid={`button-remove-tag-${tag.toLowerCase().replace(' ', '-')}`}
                      >
                        <X className="h-2 w-2" />
                      </Button>
                    </Badge>
                  ))}
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        <div className="flex gap-4">
          <Button 
            type="submit" 
            className="flex-1" 
            disabled={uploadMutation.isPending}
            data-testid="button-upload"
          >
            <Upload className="mr-2 h-4 w-4" />
            {uploadMutation.isPending ? "Uploading..." : "Upload Resource"}
          </Button>
          <Button 
            type="button" 
            variant="outline" 
            disabled={uploadMutation.isPending}
            onClick={() => {
              setFormData({ title: "", description: "", subject: "", semester: "", tags: [], currentTag: "" });
              setSelectedFile(null);
            }}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
        </div>
      </form>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

